import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  user: null,
  status: "idle", // 'success' | 'failed' | 'pending' | 'idle'
  error: null,
  isLoggedIn: false,
};

const authSlice = createSlice({
  name: "auth",
  initialState,
  reducers: {},
  extraReducers: (builder) => {},
});

const reducer = authSlice.reducer;
export default reducer;
